def auth_rc(data): # autori riscritture letterarie
    l_rc = []
    for row in data:
        if row.get('riscritture_cinematografiche') != None and row.get('riscritture_cinematografiche') != '':
            for rc in row.get('riscritture_cinematografiche'):
                if rc.get('autore_label') != None and rc.get('autore_label') != '':
                    if type(rc.get('autore_label')) == list: # c'è più di un autore per 1 opera
                        for auth in rc.get('autore_label'):
                            l_rc.append(auth)
                    if type(rc.get('autore_label')) == str: # c'è un solo autore per 1 opera
                        l_rc.append(rc.get('autore_label'))
    lista_rc = list(set(l_rc))
    #print(lista_rc)
    return lista_rc

def fonti(data, row_name): # autori riscritture letterarie
    l = []
    dictionary = {}
    for row in data:
        if row.get(row_name) != None and row.get(row_name) != '':
            for rc in row.get(row_name):
                if rc.get('autore_label') != None and rc.get('autore_label') != '':
                    if type(rc.get('autore_label')) == list: # c'è più di un autore per 1 opera
                        n = 1
                        nal = list(set(rc.get('autore_label')))
                        for auth in nal:
                            dictionary.update({'author-' + str(n): auth})
                            n = n + 1
                    if type(rc.get('autore_label')) == str: # c'è un solo autore per 1 opera
                        dictionary.update({'author-1': rc.get('autore_label')})
                if rc.get('work_label') != None and rc.get('work_label') != '':
                    dictionary.update({'work': rc.get('work_label')})
        l.append(dictionary)
    print(l)
    return l

fonti(results, 'riscritture_letterarie')

def auth_item(data):
    lista_au_item = []
    for row in data:
        if row.get('item_autore') != None and row.get('item_autore') != []:
            for ia in row.get('item_autore'):
                lista_au_item.append(ia.get('autore_label'))
    l_au_item = list(set(lista_au_item))
    #print (l_au_item)
    return l_au_item

def luoghi(data):
    l_luoghi = []
    for row in data:
        d_luoghi = {}
        if row.get('item_citta_label') != None and row.get('item_citta_label') != "":
            d_luoghi.update({'item_citta_label' : row.get('item_citta_label')})
        if row.get('item_collocazione_label') != None and row.get('item_collocazione_label') != "":
            d_luoghi.update({'item_collocazione_label' : row.get('item_collocazione_label')})
        if d_luoghi != {}:
            l_luoghi.append(d_luoghi)
    l = [dict(t) for t in {tuple(d.items()) for d in l_luoghi}]
    #print (len(l), l)
    return l


