    au_dict = {}
    au_dict.update({'l_au_fcl': auth_cl(results)})
    au_dict.update({'l_au_fmm': auth_mm(results)})
    au_dict.update({'l_au_rl': auth_rl(results)})
    au_dict.update({'l_au_rc': auth_rc(results)})
