def reconciled_tsv_matcher(tsv_path, viaf_data_column_name, autore_label_dataset):
    # Questa funzione viene richiamata poi in mythologiae_from_tsv_to_rdf nella creazione del grafo factual data
    l = []
    tsv = process_data_tsv(tsv_path)
    for dtsv in tsv:
        if dtsv.get('viaf_label') != '' and dtsv.get('viaf_id') != '':
            if dtsv.get(viaf_data_column_name) == autore_label_dataset:
                l.append(dtsv.get('viaf_label'))
                l.append(dtsv.get('viaf_id'))
    #print(row[data_column_name])  --> per controllare se funziona
    return l #ritorna una lista con viaf_label e viaf_id



